using namespace std;

vector<int> intersperse(vector<int> numbers, int delimeter) {
    // Calculate the size of the resulting vector.
    size_t result_size = numbers.size() + (numbers.size() > 0 ? numbers.size() - 1 : 0);

    // Create a new vector with the calculated size.
    vector<int> result(result_size);

    if (!numbers.empty()) {
        int pos = 0;

        for (size_t i = 0; i < numbers.size(); ++i) {
            result[pos++] = numbers[i];

            if (i != numbers.size() - 1) { // Don't add delimiter after the last element
                result[pos++] = delimeter;
            }
        }
    }

    return result;
}

