using namespace std;

vector<int> parse_nested_parens(string paren_string) {
    vector<int> depths;

    // Function to calculate depth for a single group of parentheses
    auto calculate_depth = [](const string& group) -> int {
        int max_depth = 0, current_depth = 0;

        for (char c : group) {
            if (c == '(') {
                ++current_depth;
                if (max_depth < current_depth) {
                    max_depth = current_depth;
                }
            } else if (c == ')') {
                --current_depth;
            }
        }
        return max_depth;
    };

    // Split the input string into groups and calculate depth for each group
    size_t start = 0, end = paren_string.find(' ');
    while (end != string::npos) {
        depths.push_back(calculate_depth(paren_string.substr(start, end - start)));
        start = end + 1;
        end = paren_string.find(' ', start);
    }

    // Add the last group
    if (start < paren_string.length()) {
        depths.push_back(calculate_depth(paren_string.substr(start)));
    }

    return depths;
}


