string longest(vector<string> strings) {
    if (strings.empty()) {  // Check if the vector is empty
        return "";  // Return an empty string instead of None, as C++ doesn't have a direct equivalent to Python's None for strings
    }

    string longest_str = strings[0];  // Initialize with the first string in the vector

    for (const auto& str : strings) {
        if (str.length() > longest_str.length()) {  // If current string is longer than the longest found so far
            longest_str = str;  // Update the longest string
        }
    }

    return longest_str;
}


