std::vector<int> sum_product(std::vector<int> numbers) {
    int total_sum = 0;
    int product = 1;

    if (numbers.empty()) {
        return {0, 1};
    }

    for (int number : numbers) {
        total_sum += number;
        product *= number;
    }

    return {total_sum, product};
}

