vector<int> factorize(int n) {
    vector<int> factors;

    // Handle even numbers first.
    while (n % 2 == 0) {
        factors.push_back(2);
        n /= 2;
    }

    // Check for odd factors starting from 3
    for (int i = 3; i * i <= n; i += 2) {
        while (n % i == 0) {
            factors.push_back(i);
            n /= i;
        }
    }

    // If n is a prime number greater than 2
    if (n > 2) {
        factors.push_back(n);
    }

    return factors;
}

// Function to print the vector of factors for testing purposes.
void printFactors(const vector<int>& factors) {
    cout << "{";
    for (size_t i = 0; i < factors.size(); ++i) {
        if (i > 0) cout << ", ";
        cout << factors[i];
    }
    cout << "}" << endl;
}


