#include <map>

std::string sort_numbers(std::string numbers) {
    // Mapping from string representation of numbers to their integer values
    std::map<std::string, int> number_map = {
        {"zero", 0},
        {"one", 1},
        {"two", 2},
        {"three", 3},
        {"four", 4},
        {"five", 5},
        {"six", 6},
        {"seven", 7},
        {"eight", 8},
        {"nine", 9}
    };

    // Split the input string into individual words
    std::istringstream iss(numbers);
    std::vector<std::string> number_words;
    std::string word;

    while (iss >> word) {
        number_words.push_back(word);
    }

    // Convert the list of words to integers using the mapping
    std::vector<int> int_numbers;
    for (const auto& num_word : number_words) {
        if (number_map.find(num_word) != number_map.end()) {
            int_numbers.push_back(number_map[num_word]);
        }
    }

    // Sort the vector of integer values
    std::sort(int_numbers.begin(), int_numbers.end());

    // Convert the sorted integers back to their corresponding string representations
    std::vector<std::string> sorted_str_numbers;
    for (int num : int_numbers) {
        for (const auto& entry : number_map) {
            if (entry.second == num) {
                sorted_str_numbers.push_back(entry.first);
                break;
            }
        }
    }

    // Join the sorted strings into a single space-delimited string and return it
    std::ostringstream oss;
    for (size_t i = 0; i < sorted_str_numbers.size(); ++i) {
        if (i != 0) {
            oss << " ";
        }
        oss << sorted_str_numbers[i];
    }

    return oss.str();
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (sort_numbers("") == "");
    assert (sort_numbers("three") == "three");
    assert (sort_numbers("three five nine") == "three five nine");
     assert (sort_numbers("five zero four seven nine eight") == "zero four five seven eight nine");
      assert (sort_numbers("six five four three two one zero") == "zero one two three four five six");
}

