#include <iostream>
using namespace std;

bool is_prime(long long n) {
    // Handle edge cases: numbers less than or equal to 1 are not prime.
    if (n <= 1) return false;

    // Check for divisibility by 2 and 3 first, as they are the smallest primes.
    if (n == 2 || n == 3) return true; // 2 and 3 are prime numbers
    if (n % 2 == 0 || n % 3 == 0) return false; // If divisible by 2 or 3, it's not prime

    // Check for divisibility from 5 onwards, skipping even numbers.
    long long i = 5;
    while (i * i <= n) {
        if (n % i == 0 || n % (i + 2) == 0)
            return false; // If divisible by any number in this range, it's not prime
        i += 6; // Skip even numbers and multiples of 3
    }

    return true;
}


