double poly(const std::vector<double>& xs, double x) {
    /*
    Evaluates polynomial with coefficients xs at point x. return xs[0] + xs[1] * x + xs[2] * x^2 + .... xs[n] * x^n 
    */
    double sum = 0;
    for (size_t i = 0; i < xs.size(); ++i) {
        sum += xs[i] * std::pow(x, static_cast<int>(i));
    }
    return sum;
}

double poly_derivative(const std::vector<double>& xs, double x) {
    /*
    Computes the derivative of a polynomial with coefficients xs.
    */
    std::vector<double> deriv(xs.size() - 1);
    for (size_t i = 1; i < xs.size(); ++i) {
        deriv[i-1] = static_cast<int>(i) * xs[i];
    }
    return poly(deriv, x);
}

double find_zero(const std::vector<double>& xs) {
    /*
    Finds the root of a polynomial with coefficients xs such that poly(x) = 0.
    Assumes xs has an even number of coefficients and guarantees a solution.
    */
    double tol = 1e-6;
    double initial_guess = 0.5; // Initial guess for Newton's method
    double x = initial_guess;

    while (true) {
        double fx = poly(xs, x);
        if (std::abs(fx) < tol) {
            return x;
        }

        double dfdx = poly_derivative(xs, x);  // Pass 'x' to the derivative function
        if (dfdx == 0) {
            break;
        }

        x -= fx / dfdx;
    }

    return x;
}

#undef NDEBUG
#include<assert.h>
int main(){

    double solution;
    int ncoeff;
    for (int i=0;i<100;i++)
    {
        ncoeff = 2 * (1+rand()%4);
        vector<double> coeffs = {};
        for (int j=0;j<ncoeff;j++)
        {
            double coeff = -10+rand()%21;
            if (coeff == 0) coeff = 1;
            coeffs.push_back(coeff);
        }

        solution = find_zero(coeffs);
        assert (abs(poly(coeffs, solution))< 1e-3);
    }
}

