bool triples_sum_to_zero(std::vector<int> l) {
    // Sort the vector to make it easier to find triplets
    std::sort(l.begin(), l.end());

    int n = l.size();
    for (int i = 0; i < n - 2; ++i) {
        int left = i + 1;
        int right = n - 1;

        while (left < right) {
            int sum = l[i] + l[left] + l[right];

            if (sum == 0) {
                return true;
            } else if (sum < 0) {
                ++left; // Need a larger number
            } else {
                --right; // Need a smaller number
            }
        }
    }

    return false;
}


