// Function to check if a number is prime
bool is_prime(int num) {
    if (num <= 1) return false;
    for (int i = 2; i * i <= num; ++i) {
        if (num % i == 0) return false;
    }
    return true;
}

// Function to generate the nth prime Fibonacci number
int prime_fib(int n) {
    int count = 0, fib1 = 0, fib2 = 1, fib3 = 1;

    while (true) {
        if (is_prime(fib3)) {
            ++count;
            if (count == n) return fib3; // Return the nth prime Fibonacci number
        }
        fib1 = fib2;
        fib2 = fib3;
        fib3 = fib1 + fib2; // Generate next Fibonacci number
    }

    return -1; // This line should never be reached as we always find a result.
}


