int fib4(int n) {
    if (n == 0 || n == 1)
        return 0;
    if (n == 2)
        return 2;

    int a = 0, b = 0, c = 2, d = 0; // Initial values for the first four terms
    int next_value;

    for (int i = 4; i <= n; ++i) {
        next_value = a + b + c + d;
        a = b;
        b = c;
        c = d;
        d = next_value;
    }

    return d;
}


