bool is_palindrome(string text) {
    int left = 0;
    int right = text.length() - 1;

    while (left < right) {
        if (text[left] != text[right]) {
            return false; // Characters do not match
        }
        left++;
        right--;
    }

    return true; // All characters matched, it's a palindrome
}


