#include <iostream>

int modp(int n, int p) {
    if (n == 0) return 1; // Special case for 2^0 = 1

    long result = 1;
    long base = 2;

    while (n > 0) {
        if (n % 2 == 1) { // If n is odd, multiply the current result by the base
            result = (result * base) % p;
        }
        base = (base * base) % p; // Square the base and take modulo p
        n /= 2; // Divide n by 2
    }

    return static_cast<int>(result);
}


