bool correct_bracketing(string brackets) {
    int balance = 0; // This will count the number of unmatched '<' characters.

    for (char c : brackets) {
        if (c == '<') {
            balance++; // Increment balance when an opening bracket is found.
        } else if (c == '>') {
            balance--; // Decrement balance when a closing bracket is found.

            // If at any point the balance goes negative, it means there's a closing
            // bracket without a matching opening one before it.
            if (balance < 0) {
                return false;
            }
        }
    }

    // At the end of the string, the balance should be zero for all brackets to match correctly.
    return balance == 0;
}


