#include <iostream>

int largest_prime_factor(int n) {
    int maxPrime = -1;

    // Divide n by 2 to remove all even factors
    while (n % 2 == 0) {
        maxPrime = 2;
        n /= 2;  // equivalent to n >>= 1
    }

    // Now n must be odd, start from 3 and check for prime factors.
    for (int i = 3; i * i <= n; i += 2) {
        while (n % i == 0) {
            maxPrime = i;
            n /= i;
        }
    }

    // This condition is to handle the case when n is a prime number
    // greater than 2.
    if (n > 2)
        maxPrime = n;

    return maxPrime;
}

// Function to test the largest_prime_factor function with given inputs
void check_largest_prime_factor() {
    std::cout << "Largest prime factor of 13195 is: " << largest_prime_factor(13195) << std::endl; // Should print 29
    std::cout << "Largest prime factor of 2048 is: " << largest_prime_factor(2048) << std::endl;   // Should print 2
}


