using namespace std;

bool monotonic(const vector<float>& l) {
    if (l.size() <= 1) return true; // A single element or empty list is trivially monotonic

    bool increasing = false;
    bool decreasing = false;

    for (size_t i = 1; i < l.size(); ++i) {
        if (l[i] > l[i - 1]) {
            increasing = true;
        } else if (l[i] < l[i - 1]) {
            decreasing = true;
        }

        // If both conditions are met, the sequence is neither strictly increasing nor decreasing
        if (increasing && decreasing) return false;

    }

    return true; // The sequence is either monotonically increasing or decreasing
}


