// Function to compute the derivative of a polynomial.
vector<float> derivative(vector<float> xs) {
    vector<float> result;

    // The derivative starts from the first coefficient and goes up to the last but one.
    for (size_t i = 1; i < xs.size(); ++i) {
        result.push_back(i * xs[i]);
    }

    return result;
}


