bool correct_bracketing(std::string brackets) {
    std::stack<char> stack;

    for (char c : brackets) {
        if (c == '(') {
            stack.push(c);
        } else if (c == ')') {
            if (!stack.empty()) {
                stack.pop();
            } else {
                return false;
            }
        }
    }

    // If the stack is empty, all opening brackets have been matched
    return stack.empty();
}


