vector<int> common(vector<int> l1, vector<int> l2) {
    // Sort both vectors
    sort(l1.begin(), l1.end());
    sort(l2.begin(), l2.end());

    // Remove duplicates from each vector
    auto last_unique_l1 = unique(l1.begin(), l1.end());
    l1.erase(last_unique_l1, l1.end());

    auto last_unique_l2 = unique(l2.begin(), l2.end());
    l2.erase(last_unique_l2, l2.end());

    // Vector to store common elements
    vector<int> result;

    // Find and add common elements to the result vector
    for (int num : l1) {
        if (find(l2.begin(), l2.end(), num) != l2.end()) {
            result.push_back(num);
        }
    }

    return result;
}

