int vowels_count(const std::string& s) {
    int count = 0;
    std::string lower_s = s; // Convert to lowercase for simplicity

    // Convert the string to lowercase
    std::transform(lower_s.begin(), lower_s.end(), lower_s.begin(), ::tolower);

    // Check each character in the string
    for (char ch : lower_s) {
        if (ch == 'a' || ch == 'e' || ch == 'i' || ch == 'o' || ch == 'u') {
            count++;
        }
    }

    // Special case: treat 'y' as a vowel only if it is at the end of the word
    if (!lower_s.empty() && lower_s.back() == 'y') {
        count++;
    }

    return count;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (vowels_count("abcde") == 2);
    assert (vowels_count("Alone") == 3);
    assert (vowels_count("key") == 2);
    assert (vowels_count("bye") == 1);
    assert (vowels_count("keY") == 2);
    assert (vowels_count("bYe") == 1);
    assert (vowels_count("ACEDY") == 3);

}

