using namespace std;

int fruit_distribution(string s, int n) {
    // Split the string into parts using stringstream
    istringstream iss(s);
    int num_apples, num_oranges;

    // Extract the number of apples and oranges from the string
    string part;
    if (iss >> num_apples >> part >> part >> num_oranges) {
        // Calculate the number of mangoes by subtracting the total number of apples and oranges from the total number of fruits
        int num_mangoes = n - (num_apples + num_oranges);

        return num_mangoes;
    }

    // If parsing fails, return an error or handle appropriately
    return -1;  // Indicate an error in case of invalid input format
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (fruit_distribution("5 apples and 6 oranges",19) == 8);
    assert (fruit_distribution("5 apples and 6 oranges",21) == 10);
    assert (fruit_distribution("0 apples and 1 oranges",3) == 2);
    assert (fruit_distribution("1 apples and 0 oranges",3) == 2);
    assert (fruit_distribution("2 apples and 3 oranges",100) == 95);
    assert (fruit_distribution("2 apples and 3 oranges",5) == 0);
    assert (fruit_distribution("1 apples and 100 oranges",120) == 19);
}

