#include<vector>
using namespace std;

vector<int> pluck(vector<int> arr) {
    int minEvenValue = INT_MAX; // Initialize to maximum integer value
    int minIndex = -1;          // No even number found yet

    for (int i = 0; i < arr.size(); ++i) {
        if (arr[i] % 2 == 0 && arr[i] <= minEvenValue) { // Check if the current element is an even number and less than or equal to the current minimum
            if (arr[i] < minEvenValue || minIndex > i) { // If it's smaller, or same but with a smaller index
                minEvenValue = arr[i];                    // Update the smallest even value
                minIndex = i;                             // Update the index of this value
            }
        }
    }

    if (minIndex == -1) {
        return {};  // Return an empty vector if no even number was found
    } else {
        return {minEvenValue, minIndex}; // Return the smallest even value and its index
    }
}

