std::string circular_shift(int x, int shift) {
    // Convert integer to string
    std::string num_str = std::to_string(x);

    // Determine length of number
    size_t num_length = num_str.length();

    // Handle case where shift is greater than or equal to the number of digits
    if (shift >= num_length) {
        return std::string(num_str.rbegin(), num_str.rend());
    }

    // Compute effective shift within the range [0, num_length)
    shift %= num_length;

    // Perform circular shift
    std::string shifted_str = num_str.substr(num_length - shift) + num_str.substr(0, num_length - shift);

    return shifted_str;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (circular_shift(100, 2) == "001");
    assert (circular_shift(12, 2) == "12");
    assert (circular_shift(97, 8) == "79");
    assert (circular_shift(12, 1) == "21");
    assert (circular_shift(11, 101) == "11");
}

