int search(std::vector<int> lst) {
    // Step 1: Count frequency of each integer in the vector
    std::unordered_map<int, int> freq;
    for (auto num : lst) {
        freq[num]++;
    }

    // Step 2 and 3: Find the maximum integer that satisfies the condition
    int max_val = -1; // Initialize with -1 to handle case where no such value exists
    for (const auto& entry : freq) {
        if (entry.first <= entry.second && entry.first > max_val) {
            max_val = entry.first;
        }
    }

    return max_val;
}

// Example usage and check function
void check() {
    assert(search({4, 1, 2, 2, 3, 1}) == 2);
    assert(search({1, 2, 2, 3, 3, 3, 4, 4, 4}) == 3);
    assert(search({5, 5, 4, 4, 4}) == -1);

    std::cout << "All tests passed.\n";
}


