std::vector<int> strange_sort_list(std::vector<int> lst) {
    if (lst.empty()) return lst; // Return empty vector if input is empty

    std::sort(lst.begin(), lst.end()); // Sort the list in ascending order
    std::vector<int> result;

    int left = 0, right = lst.size() - 1;
    while (left <= right) {
        if (left == right) { // If only one element is remaining
            result.push_back(lst[left]);
            break;
        }

        result.push_back(lst[left++]); // Add the minimum value and move left pointer to the right
        result.push_back(lst[right--]); // Add the maximum value and move right pointer to the left
    }

    return result;
}

