#include <vector>
using namespace std;

int smallest_change(vector<int> arr) {
    int left = 0; // Start from the beginning
    int right = arr.size() - 1; // Start from the end
    int changes = 0; // Count of required changes

    while (left < right) { // Continue until pointers meet in the middle
        if (arr[left] != arr[right]) {
            changes++; // Increment change count when elements are not equal
        }
        left++; // Move left pointer to the right
        right--; // Move right pointer to the left
    }

    return changes; // Return total number of required changes
}

