// Function to count prime hexadecimal digits in a given string.
int hex_key(const std::string& num) {
    // Define which hexadecimal characters are prime numbers.
    const char prime_hex_digits[] = {'2', '3', '5', '7', 'B', 'D'};

    int prime_count = 0;

    // Iterate through each character in the input string.
    for (char c : num) {
        // Check if the current character is a prime hexadecimal digit.
        if (std::find(std::begin(prime_hex_digits), std::end(prime_hex_digits), c) != std::end(prime_hex_digits)) {
            ++prime_count;
        }
    }

    return prime_count;
}

// Function to test and verify the correctness of the hex_key function.
void check(const std::string& num, int expected) {
    int result = hex_key(num);
    if (result == expected)
        std::cout << "PASS: For \"" << num << "\", got " << result << "\n";
    else
        std::cout << "FAIL: For \"" << num << "\", expected " << expected << ", but got " << result << "\n";
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (hex_key("AB") == 1      );
    assert (hex_key("1077E") == 2 );
    assert (hex_key("ABED1A33") == 4     );
    assert (hex_key("2020") == 2 );
    assert (hex_key("123456789ABCDEF0") == 6      );
    assert (hex_key("112233445566778899AABBCCDDEEFF00") == 12 );
    assert (hex_key("") == 0);
}

