bool iscube(int a) {
    // Calculate the cube root of 'a'
    double cbrt_a = std::cbrt(a);

    // Round to the nearest integer
    int rounded_cbrt_a = static_cast<int>(std::round(cbrt_a));

    // Check if cubing this rounded value gives back 'a'
    return (rounded_cbrt_a * rounded_cbrt_a * rounded_cbrt_a == a);
}


