    assert (iscube(1) == true);
    assert (iscube(2) == false);
    assert (iscube(-1) == true);
    assert (iscube(64) == true);
    assert (iscube(180) == false);
    assert (iscube(1000) == true);
    assert (iscube(0) == true);
    assert (iscube(1729) == false);
}

