using namespace std;

int count_chars(const vector<string>& lst) {
    int total = 0;
    for (const auto& str : lst) {
        total += str.length();
    }
    return total;
}

vector<string> total_match(vector<string> lst1, vector<string> lst2) {
    // Calculate the total number of characters in each list
    int chars_lst1 = count_chars(lst1);
    int chars_lst2 = count_chars(lst2);

    // Compare and return the appropriate list
    if (chars_lst1 < chars_lst2 || chars_lst1 == chars_lst2) {
        return lst1;
    } else {
        return lst2;
    }
}

