// Function to convert decimal number to binary and format it as specified
string decimal_to_binary(int decimal) {
    // Convert decimal to binary using bitset for simplicity
    string binary = "";

    if (decimal == 0) { // Special case for zero
        return "db0db";
    }

    while(decimal > 0) {
        int remainder = decimal % 2;
        binary = to_string(remainder) + binary; // Prepend the remainder to form the binary string
        decimal /= 2;
    }

    // Add 'db' at the beginning and end of the binary string
    return "db" + binary + "db";
}


