bool is_happy(std::string s) {
    // Check if the string length is less than 3
    if (s.length() < 3) return false;

    // Iterate through the string to check every set of three consecutive characters
    for (size_t i = 0; i < s.length() - 2; ++i) {
        // If any two out of three consecutive characters are the same, return false
        if (s[i] == s[i + 1] || s[i] == s[i + 2] || s[i + 1] == s[i + 2]) {
            return false;
        }
    }

    // If all sets of three consecutive characters are distinct, the string is happy
    return true;
}


