bool is_prime(int n) {
    if (n <= 1) return false;
    if (n == 2 || n == 3) return true;
    if (n % 2 == 0 || n % 3 == 0) return false;
    for (int i = 5; i * i <= n; i += 6) {
        if (n % i == 0 || n % (i + 2) == 0) return false;
    }
    return true;
}

bool is_multiply_prime(int a) {
    std::vector<int> prime_factors;

    // Check divisibility by 2
    while (a % 2 == 0 && prime_factors.size() < 3) {
        if (is_prime(2)) {
            prime_factors.push_back(2);
        }
        a /= 2;
    }

    // Check divisibility for odd numbers up to sqrt(a)
    for (int i = 3; i <= std::sqrt(a); i += 2) {
        while (a % i == 0 && prime_factors.size() < 3) {
            if (is_prime(i)) {
                prime_factors.push_back(i);
            }
            a /= i;
        }
    }

    // Check the remaining number
    if (a > 2 && is_prime(a)) {
        prime_factors.push_back(a);
    }

    return prime_factors.size() == 3;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (is_multiply_prime(5) == false);
    assert (is_multiply_prime(30) == true);
    assert (is_multiply_prime(8) == true);
    assert (is_multiply_prime(10) == false);
    assert (is_multiply_prime(125) == true);
    assert (is_multiply_prime(3 * 5 * 7) == true);
    assert (is_multiply_prime(3 * 6 * 7) == false);
    assert (is_multiply_prime(9 * 9 * 9) == false);
    assert (is_multiply_prime(11 * 9 * 9) == false);
    assert (is_multiply_prime(11 * 13 * 7) == true);
}

