int starts_one_ends(int n) {
    if (n == 1) {
        return 1;
    }

    // Count of numbers starting with 1
    int count_starting_with_1 = static_cast<int>(pow(10, n - 1));

    // Count of numbers ending with 1 but not starting with 1
    int count_ending_with_1_not_starting_with_1 = 9 * static_cast<int>(pow(10, n - 2));

    return count_starting_with_1 + count_ending_with_1_not_starting_with_1;
}


