// Function to convert an integer to a binary string
string intToBinary(int num) {
    if (num == 0) return "0";

    string result = "";
    while (num > 0) {
        result = (num % 2 ? "1" : "0") + result;
        num /= 2;
    }
    return result;
}

string solve(int N) {
    int sumOfDigits = 0;

    // Sum all the digits of N
    while (N > 0) {
        sumOfDigits += N % 10;  // Add the last digit to the sum
        N /= 10;                // Remove the last digit from N
    }

    // Convert the sum into a binary string and return it
    return intToBinary(sumOfDigits);
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (solve(1000) == "1");
    assert (solve(150) == "110");
    assert (solve(147) == "1100");
    assert (solve(333) == "1001");
    assert (solve(963) == "10010");
}

