int add(vector<int> lst) {
    int sum = 0;

    // Iterate through the vector starting from index 1 (the first odd index)
    for (size_t i = 1; i < lst.size(); i += 2) {
        if (lst[i] % 2 == 0) {  // Check if the element at the current index is even
            sum += lst[i];      // Add it to the sum
        }
    }

    return sum;
}


