// Function to anti-shuffle the string
string anti_shuffle(string s) {
    string result = "";  // Resultant string after sorting each word
    int n = s.length();

    for (int i = 0; i < n;) {
        if (s[i] == ' ') {  // If current character is a space, add it to the result and move to next position
            result += " ";
            ++i;
        } else {
            int j = i + 1;

            // Find the end of the word
            while (j < n && s[j] != ' ') {
                ++j;
            }

            // Sort characters in this word
            string temp = s.substr(i, j - i);
            sort(temp.begin(), temp.end());

            // Append sorted word to result
            result += temp;

            // Move to the next word
            i = j;
        }
    }

    return result;
}

// Function to test anti_shuffle function
void test_anti_shuffle() {
    cout << "Testing 'Hi': " << (anti_shuffle("Hi") == "Hi" ? "Passed" : "Failed") << endl;
    cout << "Testing 'hello': " << (anti_shuffle("hello") == "ehllo" ? "Passed" : "Failed") << endl;
    cout << "Testing 'Hello World!!!': " << (anti_shuffle("Hello World!!!") == "Hello !!!Wdlor" ? "Passed" : "Failed") << endl;
}


