// Helper function to trim whitespace from both ends of the string
string trim(const string& str) {
    size_t first = str.find_first_not_of(' ');
    if (first == string::npos)
        return "";

    size_t last = str.find_last_not_of(' ');
    return str.substr(first, (last - first + 1));
}

int is_bored(string S) {
    int boredom_count = 0;

    // Split the string into sentences using '.', '?' or '!'
    vector<string> sentences;
    stringstream ss(S);
    string token;
    while (getline(ss, token, '.')) {
        for (char c : { '?', '!' }) {
            size_t start_pos = 0;
            while ((start_pos = token.find(c, start_pos)) != string::npos) {
                sentences.push_back(token.substr(start_pos + 1));
                start_pos += 1; // Move past the delimiter
            }
        }
        if (!token.empty()) {
            sentences.push_back(token);
        }
    }

    for (auto& sentence : sentences) {
        size_t pos = sentence.find_first_of("?.!");
        string trimmed_sentence;
        if (pos != string::npos) {
            trimmed_sentence = trim(sentence.substr(0, pos));
        } else {
            trimmed_sentence = trim(sentence);
        }

        // Check if the sentence starts with "I "
        if (!trimmed_sentence.empty() && trimmed_sentence[0] == 'I' && 
            (trimmed_sentence.size() > 1 && trimmed_sentence[1] == ' ') || 
            trimmed_sentence.size() == 1) {
            boredom_count++;
        }
    }

    return boredom_count;
}


