struct Coordinate {
    int row;
    int col;

    // Constructor for coordinate initialization
    Coordinate(int r = 0, int c = 0) : row(r), col(c) {}

    // Comparator function to sort coordinates first by row in ascending order,
    // and then by column in descending order within the same row.
    bool operator<(const Coordinate& other) const {
        if (row == other.row)
            return col > other.col;  // Descending order for columns
        else
            return row < other.row;  // Ascending order for rows
    }
};

std::vector<std::vector<int>> get_row(std::vector<std::vector<int>> lst, int x) {
    std::vector<Coordinate> coordinates;

    // Iterate through the list to find all occurrences of x and record their positions.
    for (int row = 0; row < lst.size(); ++row) {
        for (int col = 0; col < lst[row].size(); ++col) {
            if (lst[row][col] == x)
                coordinates.push_back(Coordinate(row, col));
        }
    }

    // Sort the coordinates as per the given criteria.
    std::sort(coordinates.begin(), coordinates.end());

    // Convert Coordinate objects to vector<int> and return
    std::vector<std::vector<int>> result;
    for (const auto& coord : coordinates) {
        result.push_back({coord.row, coord.col});
    }

    return result;
}

