std::vector<int> sort_array(std::vector<int> array) {
    // If the vector is empty or has only one element, return it as is.
    if (array.empty() || array.size() == 1) {
        return array;
    }

    // Calculate the sum of the first and last elements.
    int sum = array.front() + array.back();

    // Create a copy of the vector to sort.
    std::vector<int> sorted_array(array);

    // Sort in ascending order if the sum is odd, otherwise sort in descending order.
    if (sum % 2 == 1) {
        std::sort(sorted_array.begin(), sorted_array.end());
    } else {
        std::sort(sorted_array.rbegin(), sorted_array.rend());
    }

    return sorted_array;
}

// Example usage and check function
void check() {
    assert(sort_array({}) == std::vector<int>({}));
    assert(sort_array({5}) == std::vector<int>({5}));
    assert(sort_array({2, 4, 3, 0, 1, 5}) == std::vector<int>({0, 1, 2, 3, 4, 5}));
    assert(sort_array({2, 4, 3, 0, 1, 5, 6}) == std::vector<int>({6, 5, 4, 3, 2, 1, 0}));

    printf("All tests passed.\n");
}


