// Function to check if a number is prime
bool isPrime(int n) {
    if (n <= 1) return false;
    for (int i = 2; i * i <= n; ++i) {
        if (n % i == 0) return false;
    }
    return true;
}

// Function to calculate the sum of digits of a number
int digitSum(int n) {
    int sum = 0;
    while (n > 0) {
        sum += n % 10;
        n /= 10;
    }
    return sum;
}

// Main function that finds and returns the sum of digits of the largest prime in the list
int skjkasdkd(vector<int> lst) {
    int maxPrime = -1; // Initialize with a value less than any possible prime number

    for (const auto& num : lst) {
        if (isPrime(num)) {
            maxPrime = max(maxPrime, num);
        }
    }

    return digitSum(maxPrime); // Return the sum of digits of the largest prime
}


