using namespace std;

// Function to swap case of a character
char swapCase(char ch) {
    if (islower(ch)) return toupper(ch);
    else return tolower(ch);
}

// Function to shift vowels by 2 places ahead in the alphabet
char shiftVowel(char vowel) {
    string lower_vowels = "aeiou";
    string upper_vowels = "AEIOU";

    if (islower(vowel)) {
        size_t pos = lower_vowels.find(vowel);
        return pos != string::npos ? lower_vowels[(pos + 2) % 5] : vowel;
    } else {
        size_t pos = upper_vowels.find(vowel);
        return pos != string::npos ? upper_vowels[(pos + 2) % 5] : vowel;
    }
}

string encode(string message) {
    for (char &ch : message) {
        // Swap case
        ch = swapCase(ch);

        // Shift vowels by 2 places ahead
        if (isalpha(ch)) {
            ch = shiftVowel(ch);
        }
    }

    return message;
}


