bool isLowercase(const std::string& str) {
    for (char c : str) {
        if (!islower(c)) return false;
    }
    return true;
}

bool isUppercase(const std::string& str) {
    for (char c : str) {
        if (!isupper(c)) return false;
    }
    return true;
}

bool check_dict_case(std::map<std::string, std::string> dict) {
    // Check if the map is empty
    if (dict.empty()) return false;

    bool allLowercase = true;
    bool allUppercase = true;

    for (const auto& pair : dict) {
        const std::string& key = pair.first;

        // Determine case consistency
        if (!isLowercase(key)) allLowercase = false;
        if (!isUppercase(key)) allUppercase = false;

        // If both flags are already false, no need to continue checking
        if (!allLowercase && !allUppercase) break;
    }

    return allLowercase || allUppercase;
}


