vector<int> make_a_pile(int n) {
    vector<int> pile;
    int current_stones = n;  // Start with 'n' stones for the first level.

    // Add the number of stones for each level to the vector.
    for (int i = 0; i < n; ++i) {
        pile.push_back(current_stones);

        // Determine if we need an odd or even next number based on whether 'n' is odd/even.
        int increment = (n % 2 == 1) ? 2 : 2;
        current_stones += increment;  // Increment by 2 to get the next odd/even number.
    }

    return pile;
}

