int choose_num(int x, int y) {
    // Ensure x is less than or equal to y
    if (x > y) return -1;

    // Adjust y to be the largest even number in the range [x, y]
    if (y % 2 != 0) { 
        y--; // If y is odd, decrement it by 1 to make it even.
    }

    // Check if the adjusted y is still within or equal to x
    if (y >= x) {
        return y;
    } else {
        return -1; // No even number in range [x, y]
    }
}


