vector<string> words_string(string s) {
    vector<string> result;
    string word = "";

    // Iterate through each character in the input string
    for (char c : s) {
        if (c == ',' || c == ' ') {
            // If the current character is a comma or space, add the collected word to the result and reset it
            if (!word.empty()) {
                result.push_back(word);
                word = "";
            }
        } else {
            // Otherwise, append the character to the current word
            word += c;
        }
    }

    // Add any remaining word after the loop (in case there's no trailing comma or space)
    if (!word.empty()) {
        result.push_back(word);
    }

    return result;
}

