// Function to convert an integer to a binary string
string int_to_binary(int num) {
    if (num == 0) return "0";

    string result = "";
    while (num > 0) {
        result = to_string(num % 2) + result;
        num /= 2;
    }
    return result;
}

// Main function to calculate the rounded average and convert it to binary
string rounded_avg(int n, int m) {
    if (n > m) return "-1";

    // Calculate the sum of integers from n to m
    long total_sum = ((long)m - n + 1) * (n + m) / 2;

    // Calculate the average and round it down
    int rounded_avg = floor(total_sum / (m - n + 1));

    return int_to_binary(rounded_avg);
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (rounded_avg(1, 5) == "11");
    assert (rounded_avg(7, 13) == "1010");
    assert (rounded_avg(964,977) == "1111001010");
    assert (rounded_avg(996,997) == "1111100100");
    assert (rounded_avg(560,851) == "1011000001");  
    assert (rounded_avg(185,546) == "101101101");
    assert (rounded_avg(362,496) == "110101101");
    assert (rounded_avg(350,902) == "1001110010");
    assert (rounded_avg(197,233) == "11010111");
    assert (rounded_avg(7, 5) == "-1");
    assert (rounded_avg(5, 1) == "-1");
    assert (rounded_avg(5, 5) == "101");
}

