// Helper function to calculate factorial
int factorial(int i) {
    if (i == 0 || i == 1)
        return 1;
    int result = 1;
    for (int j = 2; j <= i; ++j) {
        result *= j;
    }
    return result;
}

// Helper function to calculate the sum of numbers from 1 to i
int sum_of_numbers(int i) {
    return (i * (i + 1)) / 2;
}

// Main function that returns a vector based on the given rules
std::vector<int> f(int n) {
    std::vector<int> result(n);
    for (int i = 1; i <= n; ++i) {
        if (i % 2 == 0) {
            result[i - 1] = factorial(i);
        } else {
            result[i - 1] = sum_of_numbers(i);
        }
    }
    return result;
}

