// Function to check if a number has any even digit
bool has_odd_digits(int n) {
    while (n > 0) {
        int digit = n % 10;
        if (digit % 2 == 0) return false; // If the digit is even, return false
        n /= 10; // Move to the next digit
    }
    return true; // All digits are odd
}

// Function to filter and sort numbers with only odd digits
std::vector<int> unique_digits(std::vector<int> x) {
    std::vector<int> result;

    for (int num : x) {
        if (has_odd_digits(num)) {
            result.push_back(num);
        }
    }

    // Sort the resulting vector in increasing order
    std::sort(result.begin(), result.end());

    return result;
}

// Function to test and print results
void test() {
    std::vector<int> example1 = {15, 33, 1422, 1};
    std::vector<int> expected1 = {1, 15, 33};
    assert(unique_digits(example1) == expected1);

    std::vector<int> example2 = {152, 323, 1422, 10};
    std::vector<int> expected2;
    assert(unique_digits(example2) == expected2);

    std::cout << "All tests passed successfully.\n";
}


