// Function to check if a given integer is a palindrome.
bool is_palindrome(int num) {
    string str_num = to_string(num);
    int left = 0;
    int right = str_num.length() - 1;

    while (left < right) {
        if (str_num[left] != str_num[right]) {
            return false;
        }
        left++;
        right--;
    }

    return true;
}

vector<int> even_odd_palindrome(int n) {
    vector<int> result(2, 0); // Initialize the result vector with two elements: [even_count, odd_count]

    for (int i = 1; i <= n; ++i) {
        if (is_palindrome(i)) { // Check if the number is a palindrome
            if (i % 2 == 0) {
                result[0]++; // Increment even count
            } else {
                result[1]++; // Increment odd count
            }
        }
    }

    return result;
}

